import { Component, OnInit } from '@angular/core';
import { UserService } from '../_service/user.service';

@Component({
  selector: 'app-class-library',
  templateUrl: './classLibrary.component.html',
})
export class ClassLibraryComponent implements OnInit {
  public tree = '';
  public node = '';
  public resultTree = false;
  public resultNode = 0;
  constructor(private userService: UserService) { }

  ngOnInit(): void {
  }

  onShow() {
    alert('Show button clicked!');
  }

  onClickTree(): void {
    this.userService.treeConstructor(this.tree).subscribe(
      data => {
        console.log(data);
        this.resultTree = data;
      },
      err => {
        console.log(err);
        this.resultTree = false;
      }
    );
  }
}
<div class="row">
    <h3 class="col-12 text-center">Biblioteca de clases</h3>
    <div class="col-sm-12 col-md-6 mt-4">
      <h4>Constructor de árboles</h4>
      <label>Arreglo:</label>
      <input type="text" class="form-control form-control-sm" [(ngModel)]="tree"/>
      <button type="button" (click)="onClickTree()">Verificar</button>
      <div class="alert alert-success mt-1">
        {{ resultTree }}
      </div>
    </div>
    <div class="col-sm-12 col-md-6 mt-4">
      <h4>Encuentra el nodo más lejano</h4>
      <label>Arreglo:</label>
      <input type="text" class="form-control form-control-sm" [(ngModel)]="tree" />
      <button type="button" (click)="onClickTree()">Verificar</button>
      <div class="alert alert-success mt-1">
        {{ resultNode }}
      </div>
    </div>
</div>
